import React from 'react';
import {
    Link,
} from 'react-router-dom'


export default function CountriesList(props){

    if (!props.isFiltered){

            return (
                    <div>

                    <div id="num">
                        <h5 style={{color: conditionalStyling(props.activeView)}}><strong>total of {props.activeView}:</strong></h5> 
                        <h3 style={{color: conditionalStyling(props.activeView)}}><strong>{props[props.activeView]}</strong></h3>
                    </div>
                        <ol>
                        {props.countries.sort((a,b)=> b[props.activeView] - a[props.activeView])
                            .slice(0,40)
                            .map((element)=>(
                                <li key = {element.countryInfo._id}>
                                    <Link id="link" to={`/${element.country}`}> <strong>{element.country}</strong> : {element[props.activeView]} </Link>
                                </li>
                            ))}
                        </ol>   
                    </div>
            )

    }

    if (props.isFiltered){
        if(props.activeView === 'cases'){
            return (
                <div>
                    <div id="num">
                        <h5 style={{color: 'orange'}}><strong>total of cases:</strong></h5> 
                        <h3 style={{color: 'orange'}}><strong>{props[props.activeView]}</strong></h3>
                    </div>
                        <ul>
                            {props.countries.filter((country)=>country.country === props.input)
                                .map((element)=>(
                                    <li key = {element.countryInfo._id}>
                                        <Link id="link" to={`/${element.country}`}> <strong>{element.country}</strong> : {element.cases} </Link>
                                    </li>
                                ))}
                        </ul>   
                </div>)

        }

        if(props.activeView === 'recovered'){
            return (
                <div>
                    <div id="num">
                        <h5 style={{color: 'green'}}><strong>total of recovered:</strong></h5> 
                        <h3 style={{color: 'green'}}><strong>{props.recovered}</strong></h3>
                    </div>
                        <ul>
                            {props.countries.filter((country)=>country.country === props.input)
                                .map((element)=>(
                                    <li key = {element.countryInfo._id}>
                                        <Link id="link" to={`/${element.country}`}> <strong>{element.country}</strong> : {element.recovered} </Link>
                                    </li>
                                ))}
                        </ul>   
                </div>) 

        }

        if(props.activeView === 'deaths'){

            return (
                <div>
                    <div id="num">
                        <h5 style={{color: 'red'}}><strong>total of deaths:</strong></h5> 
                        <h3 style={{color: 'red'}}><strong>{props.deaths}</strong></h3>
                    </div>
                        <ul>
                            {props.countries.filter((country)=>country.country === props.input)
                                .map((element)=>(
                                    <li key = {element.countryInfo._id}>
                                        <Link id="link" to={`/${element.country}`}> <strong>{element.country}</strong> : {element.deaths} </Link>
                                    </li>
                                ))}
                        </ul> 
                </div>)
        }
    }


}

function conditionalStyling(activeView){
    if (activeView === 'cases'){
        return "orange"
    }
    if (activeView === 'deaths'){
        return "red"
    }
    return "green"
}
