import React from 'react';
import {useParams, useHistory} from 'react-router-dom'
import {useEffect, useState} from 'react'





export default function Country() {

    const [country, setCountry] = useState({}) 
    const {countryID} = useParams();

        async function fetchData(){
            const data = await fetch(`https://disease.sh/v2/countries/${countryID}`)

            return data.json()
        }

        useEffect(()=>{

            fetchData().then((data)=> setCountry(data))
        })



    const history = useHistory();
    return (
        <div>
        <span><h3><strong>{(country.country)}</strong></h3></span>
        <button onClick={()=>{history.goBack()}} ><i class="fas fa-times-circle"/></button>

        </div>

    )


}
