import React from 'react';

async function fetchGlobalData(){
    const api = `https://disease.sh/v2/all`;
    const response = await fetch(api);
    const content = await response.json();

    console.log(content)

    return content;
  }

export default class Home extends React.Component {

    constructor(props){
        super(props)
        this.state = {
            casesNum: 0,
            recoveredNum: 0,
            deathsNum: 0,
            activeView: 'cases'
        }
        this.fetchData = this.fetchData.bind(this)
    }

    componentDidMount(){
        this.fetchData()
    }

    componentDidUpdate(prevProps, prevState){
        if (prevState.activeView !== this.state.activeView)
        this.fetchData
    }

    fetchData(){

        fetchGlobalData()
        .then((data) =>{
            this.setState({
                casesNum: data.cases,
                recoveredNum: data.recovered,
                deathsNum: data.deaths
            })
        })
    }

    render(){
        return (
            <div>
            <h3>Cases: {this.state.casesNum}</h3>
            <h3>Recovered: {this.state.recoveredNum}</h3>
            <h3>Deaths: {this.state.deathsNum}</h3>
            </div>
        )

    }




}
