import React from 'react';


export default function CountriesList(props){

    if (props.activeView === 'cases'){
        return (
            <div>

            <h1>{props.casesNum}</h1>
            <ul>
                {props.countries.sort((a,b)=> b.cases - a.cases)
                    .slice(0,10)
                    .map((element)=>(
                    <li key = {element.countryInfo._id}>
                        {element.country} : {element.cases}
                    </li>
                ))}
            </ul>   
            </div>
        )
    }

    if (props.activeView === 'deaths'){
        return (
            <div>
            <h1>{props.deathsNum}</h1>
            <ul>
                {props.countries.sort((a,b)=> b.deaths - a.deaths)
                    .slice(0,10)
                    .map((element)=>(
                    <li key = {element.countryInfo._id}>
                        <strong>{element.country}</strong> : {element.deaths}
                    </li>
                ))}
            </ul>   
            </div>
        )
    }

    if (props.activeView === 'recovered'){
        return (
            <div>
            <h1>{props.recoveredNum}</h1>
            <ul>
                {props.countries.sort((a,b)=> b.recovered - a.recovered)
                    .slice(0,10)
                    .map((element)=>(
                    <li key = {element.countryInfo._id}>
                        <strong></strong>{element.country} : {element.recovered}
                    </li>
                ))}
            </ul>   
            </div>
        )
    }
}
