import React from 'react';
import {useState, useEffect} from 'react';
import {
    AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip, Legend
} from 'recharts'




export default function GlobalChart(){

    const [casesData, setCasesData] = useState({});


    useEffect(()=>{

        async function fetchData(){
            const requestOption = {
                method: "GET",
                redirect: "follow"
            }

            try {
                const response = await fetch(`https://disease.sh/v2/historical/all`, requestOption)

                return response.ok ? response.json() : null
            } catch (err){
                console.log(err);
                return null;
            }
        }

        async function prepareData(){
            let apiValues = await fetchData();
            let rawCasesData = apiValues["cases"];

            const chartCasesData = []

            for (let key in rawCasesData){

                if(rawCasesData.hasOwnProperty(key)){
                    const newRow = {
                        name: key,
                        value: parseInt(rawCasesData[key])
                    }
                    chartCasesData.push(newRow)
                    }
                }

            setCasesData(chartCasesData)

            }

            prepareData();

    }, [])




    return (
        <div id="homeChart" className="chart-wrapper">
        <h5></h5>
        <AreaChart width={970} height={450} data={casesData}
        margin={{ top: 70, right: 10, left: 30, bottom: 70 }}>
        <defs>
          <linearGradient id="colorUv" x1="0" y1="0" x2="0" y2="1">
            <stop offset="5%" stopColor="#FF8C00" stopOpacity={0.8}/>
            <stop offset="95%" stopColor="#FF8C00" stopOpacity={0}/>
          </linearGradient>

        </defs>
        <XAxis dataKey="name" />
        <YAxis />
        <CartesianGrid strokeDasharray="3 3" />
        <Tooltip />
        <Area type="monotone" dataKey="value" stroke="#FF8C00" fillOpacity={1} fill="url(#colorUv)" />

      </AreaChart>



        </div>
        )

}
