import React from 'react';
import {useParams, useHistory} from 'react-router-dom'
import {useEffect, useState} from 'react'





export default function Country() {

    const [country, setCountry] = useState({})
    const [flag, setFlag] = useState({}) 

    const {countryID} = useParams();

        async function fetchData(){
            const data = await fetch(`https://disease.sh/v2/countries/${countryID}`)

            return data.json()
        }

        useEffect(()=>{
            let isMounted= true;

            fetchData().then((data)=> {
                if (isMounted){
                    setCountry(data)
                    setFlag(data.countryInfo.flag)
                }    
            })
            return () => isMounted = false}, []);


        const history = useHistory();
        return (
            <div id="country-card">
                <h3><strong>{country.country}</strong></h3>

                <img src={flag} alt="what"></img>

                <div id="details" style="float:leftt">


                </div>

                <p>{country.cases}</p>





                <br></br>
                <button id="homeBtn" onClick={()=>{history.goBack()}} ><i className="fas fa-home"/></button>
            </div>

        )

    }
