import React from 'react';

export default function NavBar(props){
    const viewNames = ['cases', 'deaths', 'recovered']

    const style ={
        style: "background-color: #e3f2fd;" 
    }
    return(
        <div id='nav'>
        <nav className="navbar navbar-light" style={style}>

        {viewNames.map((viewName) =>(

            <button type="button" className="btn btn-primary btn-lg" id="nav-btns" key={viewName} onClick={()=> props.onSelectView(viewName)}>
            {viewName}</button>

            ))}

            </nav>
            </div>

    )

}
