import React from 'react';
import {useParams, useHistory} from 'react-router-dom'
import {useEffect, useState} from 'react'





export default function Country() {

    const [country, setCountry] = useState({})
    const [flag, setFlag] = useState({}) 

    const {countryID} = useParams();

        async function fetchData(){
            const data = await fetch(`https://disease.sh/v2/countries/${countryID}`)

            return data.json()
        }

        useEffect(()=>{
            let isMounted= true;

            fetchData().then((data)=> {
                if (isMounted){
                    setCountry(data)
                    setFlag(data.countryInfo.flag)
                }    
            })
            return () => isMounted = false}, []);


        const history = useHistory();
        return (
            <div id="country-card">
                <h3><strong>{country.country}</strong></h3>
                <h5>_in <em>{country.continent}</em></h5>

                <table>
                <thead>
                    <tr>
                        <td><img src={flag} alt="flag of the country in question"></img></td>
                            <td>

                            <p>{country.cases} cases - <strong>of which {country}</strong></p>
                            <p>{country.deaths} deaths - <strong>{country.todayDeaths} new deaths</strong> </p>
                            <p>{country.recovered} recovered</p>
                            <p>{country.tests} tests</p>
                        </td> 
                    </tr>
                </thead>
                </table>















                <br></br>
                <button id="homeBtn" onClick={()=>{history.goBack()}} ><i className="fas fa-home"/></button>
            </div>

        )

    }
