import React from 'react';
import CountriesList from './CountriesList.js'

async function fetchGlobalData(){
    const api = `https://disease.sh/v2/all`;
    const response = await fetch(api);
    const content = await response.json();

    console.log(content)

    return content;
}

async function fetchCountriesData(){
    const api = `https://disease.sh/v2/countries`;
    const response = await fetch(api);
    const content = await response.json();

    return content;
}

export default class Home extends React.Component {

    constructor(props){
        super(props)
        this.state = {
            casesNum: 0,
            recoveredNum: 0,
            deathsNum: 0,
            activeView: 'cases',
            countries: []
        }
        this.handleGlobalData = this.handleGlobalData.bind(this)
        this.handleCountriesData = this.handleCountriesData.bind(this)
    }

    componentDidMount(){
        this.handleGlobalData()
        this.handleCountriesData();
    }

    componentDidUpdate(prevProps, prevState){
        if (prevState.activeView !== this.state.activeView){
            this.handleGlobalData();
            this.handleCountriesData();
        }
    }

    handleGlobalData(){
        fetchGlobalData()
        .then((data) =>{
            this.setState({
                casesNum: data.cases,
                recoveredNum: data.recovered,
                deathsNum: data.deaths
            })
        })
    }

    handleCountriesData(){
        fetchCountriesData()
        .then((data)=>{
            this.setState({
                countries: data
            })
        })
    }

    handleSelectView(viewName){
        this.setState({
            activeView: viewName
        })
    }

    render(){
        return (
            <div>
                <h3>Cases: {this.state.casesNum}</h3>
                <h3>Recovered: {this.state.recoveredNum}</h3>
                <h3>Deaths: {this.state.deathsNum}</h3>

                <div>
                    <h3>Countries Report: </h3>
                    <CountriesList
                        countries={this.state.countries}
                        activeView={this.state.activeView}
                    />
                </div>

            </div>
        )
    }   
}
