import React from 'react';
import {useState, useEffect} from 'react';
import {browserHistory} from 'react-router';


function catchError(){
    //browserHistory.push('/page-not-found')
    //window.location.reload()
    console.log(1)
}

export default function GlobalChart(){

    const [casesData, setCasesData] = useState({});
    const [deathsData, setDeathsData] = useState({});
    const [recoveredData, setRecoveredData] = useState({});

    useEffect(()=>{

        async function fetchData(){
            const requestOption = {
                method: "GET",
                redirect: "follow"
            }

            try {
                const response = await fetch(`https://disease.sh/v2/historical/all`, requestOption)

                return response.ok ? response.json() : null
            } catch (err){
                console.log(err);
                return null;
            }
        }

        async function prepareData(){
            let apiValues = await fetchData();
            let rawCasesData = apiValues["cases"];

            const casesData = []

            for (let key in rawCasesData){

                if(rawCasesData.hasOwnProperty(key)){
                    const newRow = {
                        name: key,
                        value: rawCasesData[key]
                    }
                    casesData.push(newRow)
                    }
                }
            setCasesData(casesData)

            }

            prepareData();

    }, [])




    return (
        <div>
        <h3>
        WHAT
        </h3>
        </div>
        )

}
