import React from 'react';
import CountryPage from './CountryPage.js'
import {
    Route,
    Link
} from 'react-router-dom'


export default function CountriesList(props){

    if (props.activeView === 'cases'){
        return (
            <div id="container">

            <div id="num">
                <h3>{props.casesNum}</h3>
            </div>
            <ul>
                {props.countries.sort((a,b)=> b.cases - a.cases)
                    .slice(0,10)
                    .map((element)=>(
                    <li key = {element.countryInfo._id}>
                        <Link to={`/${element.country}`}>{element.country}</Link>
                        <strong>{element.country}</strong> : {element.cases} 
                        <Route path={`${props.match.path}/:countryName`} render={()=>{
                            return (
                                <CountryPage
                                />
                            )
                        }}/>
                    </li>

                ))}
            </ul>   
            </div>
        )
    }

    if (props.activeView === 'deaths'){
        return (
            <div id="container">
            <div id="num">
                <h3>{props.deathsNum}</h3>
            </div>
            <ul>
                {props.countries.sort((a,b)=> b.deaths - a.deaths)
                    .slice(0,10)
                    .map((element)=>(
                    <li key = {element.countryInfo._id}>
                        <strong>{element.country}</strong> : {element.deaths}
                    </li>
                ))}
            </ul>   
            </div>
        )
    }

    if (props.activeView === 'recovered'){
        return (
            <div id="container">
            <div id="num">
                <h3>{props.recoveredNum}</h3>
            </div>
            <ul>
                {props.countries.sort((a,b)=> b.recovered - a.recovered)
                    .slice(0,10)
                    .map((element)=>(
                    <li key = {element.countryInfo._id}>
                        <strong>{element.country}</strong> : {element.recovered}
                    </li>
                ))}
            </ul>   
            </div>
        )
    }
}
